mapper.select:
SELECT NVL(SUM(product_price * amount), 0) money
FROM tbl_cart c, tbl_product p
WHERE c.product_id = p.product_id AND c.user_id = #{param1}

SELECT COUNT(*)
FROM tbl_cart
WHERE user_id= #{userId} 
AND product_id = #{productId}

mapper.update:
UPDATE tbl_cart 
SET amount = amount + #{amount} 
WHERE user_id= #{userId} 
AND product_id = #{productId}
